  function getRandomTenElements(arr) {
    // 检查数组是否存在且长度是否足够
    if (!Array.isArray(arr) || arr.length < 10) {
        return { error: "模板不足" };
    }

    // 创建一个副本数组，避免修改原数组
    const copiedList = [...arr];
    const newList = [];

    // 随机抽取 10 个元素
    for (let i = 0; i < 10; i++) {
        const randomIndex = Math.floor(Math.random() * copiedList.length);
        newList.push(copiedList[randomIndex]);
        copiedList.splice(randomIndex, 1); // 从副本数组中移除已抽取的元素
    }

    // 返回结果
    return {
        changedList: copiedList, // 剩余的数组
        newList: newList // 随机抽取的 10 个元素
    };
}

